package nl.springbank.bean;

import javax.persistence.*;
import javax.validation.constraints.NotNull;

/**
 * Description
 *
 * @author Tristan de Boer).
 */
@Entity
@Table(name = "bank_account")
public class BankAccountBean {
     /*
        Private methods
     */

    // Autogenerated id (unique for each bank_account in the db)
    @Id
    @Column(name = "account_id")
    @GeneratedValue(strategy = GenerationType.AUTO)
    private long bankAccountId;

    // The bank account holder id
    @NotNull
    @Column(name = "holder_user_id")
    private int holderUserId;

    // The IBAN of the account
    @NotNull
    private String iban;

    // The balance on the account
    @NotNull
    private double balance;

    /*
        Public methods
     */

    public BankAccountBean() {
    }

    public long getBankAccountId() {
        return bankAccountId;
    }

    public void setBankAccountId(long bankAccountId) {
        this.bankAccountId = bankAccountId;
    }

    public int getHolderUserId() {
        return holderUserId;
    }

    public void setHolderUserId(int holderUserId) {
        this.holderUserId = holderUserId;
    }

    public String getIban() {
        return iban;
    }

    public void setIban(String iban) {
        this.iban = iban;
    }

    public double getBalance() {
        return balance;
    }

    public void setBalance(double balance) {
        this.balance = balance;
    }

    @Override
    public String toString() {
        return "BankAccountBean{" +
                "bankAccountId=" + bankAccountId +
                ", holderUserId=" + holderUserId +
                ", iban='" + iban + '\'' +
                ", balance=" + balance +
                '}';
    }
}
